$HOSTNAME = "@@{AD.NODE1_NAME}@@"

function Set-Hostname{
  [CmdletBinding()]
  Param(
      [parameter(Mandatory=$true)]
      [string]$Hostname
)
  if ($Hostname -eq  $(hostname)){
    Write-Host "Hostname already set."
  } else{
    Rename-Computer -NewName $HOSTNAME -ErrorAction Stop
  }
}

function JointoDomain {
  [CmdletBinding()]
  Param(
      [parameter(Mandatory=$true)]
      [string]$DomainName,
      [parameter(Mandatory=$false)]
      [string]$OU,
      [parameter(Mandatory=$true)]
      [string]$Username,
      [parameter(Mandatory=$true)]
      [string]$Password,
      [parameter(Mandatory=$true)]
      [string]$Server
  )
  $adapter = Get-NetAdapter | ? {$_.Status -eq "up"}
  $adapter | Set-DnsClientServerAddress -ServerAddresses $Server

  if ($env:computername  -eq $env:userdomain) {
    Write-Host "Not in domain"
    $adminname = "$DomainName\$Username"
    $adminpassword = ConvertTo-SecureString -asPlainText -Force -String "$Password"
    Write-Host "$adminname , $password"
    $credential = New-Object System.Management.Automation.PSCredential($adminname,$adminpassword)
    Add-computer -DomainName $DomainName -Credential $credential -force -Options JoinWithNewName,AccountCreate -PassThru -ErrorAction Stop
  } else {
     Write-Host "Already in domain"
  }
}

if ($HOSTNAME -ne $Null){
  Write-Host "Setting Hostname"
  Set-Hostname -Hostname $HOSTNAME
}

JointoDomain -DomainName "workshop.local" -Username "@@{windows_ad.username}@@" -Password "@@{windows_ad.secret}@@" -Server "@@{AD_IP}@@"

Restart-Computer -Force -AsJob
exit 0

echo "installing Antivirus..."
<#
# This script detects platform and architecture.  It then downloads and installs the relevant Deep Security Agent 10 package
if (-NOT ([Security.Principal.WindowsPrincipal] [Security.Principal.WindowsIdentity]::GetCurrent()).IsInRole([Security.Principal.WindowsBuiltInRole] "Administrator")) {
   Write-Warning "You are not running as an Administrator. Please try again with admin privileges."
   exit 1 }
$env:LogPath = "$env:appdata\Trend Micro\Deep Security Agent\installer"
New-Item -path $env:LogPath -type directory
Start-Transcript -path "$env:LogPath\dsa_deploy.log" -append
echo "$(Get-Date -format T) - DSA download started"
[Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12;
$baseUrl="https://P-HQ-VM-DSM-02.moe.local:4119/"
if ( [intptr]::Size -eq 8 ) { 
   $sourceUrl=-join($baseurl, "software/agent/Windows/x86_64/") }
else {
   $sourceUrl=-join($baseurl, "software/agent/Windows/i386/") }
echo "$(Get-Date -format T) - Download Deep Security Agent Package" $sourceUrl
Try
{
    (New-Object System.Net.WebClient).DownloadFile($sourceUrl,  "$env:temp\agent.msi")
}
Catch [System.Net.WebException] {
echo "TLS certificate validation for the agent package download has failed. Please check that your Deep Security Manager TLS certificate is signed by a trusted root certificate authority. For more information, search for `"deployment scripts`" in the Deep Security Help Center."
 exit 2;
}
if ( (Get-Item "$env:temp\agent.msi").length -eq 0 ) {
echo "Failed to download the Deep Security Agent. Please check if the package is imported into the Deep Security Manager. "
 exit 1 }
echo "$(Get-Date -format T) - Downloaded File Size:" (Get-Item "$env:temp\agent.msi").length
echo "$(Get-Date -format T) - DSA install started"
echo "$(Get-Date -format T) - Installer Exit Code:" (Start-Process -FilePath msiexec -ArgumentList "/i $env:temp\agent.msi /qn ADDLOCAL=ALL /l*v `"$env:LogPath\dsa_install.log`"" -Wait -PassThru).ExitCode 
echo "$(Get-Date -format T) - DSA activation started"
Start-Sleep -s 50
& $Env:ProgramFiles"\Trend Micro\Deep Security Agent\dsa_control" -r
& $Env:ProgramFiles"\Trend Micro\Deep Security Agent\dsa_control" -a dsm://P-HQ-VM-DSM-02.moe.local:4120/ "policyid:22"
Stop-Transcript
echo "$(Get-Date -format T) - DSA Deployment Finished"

Restart-Computer -Force -AsJob
#>

#Script can be used as a task in calm to install SQL Server - tested on Windows 2k12R2 and SQL Server 2014 SP3 on AHV

$DriveLetter = $(Get-Partition -DiskNumber 1 -PartitionNumber 2 | select DriveLetter -ExpandProperty DriveLetter)

$silentArgs = "/IACCEPTSQLSERVERLICENSETERMS /Q /ACTION=install /FEATURES=SQLENGINE,CONN,IS,BC,SDK,BOL /SECURITYMODE=sql /SAPWD=`"@@{sql_local.secret}@@`" /ASSYSADMINACCOUNTS=`"@@{sql_local.username}@@`" /SQLSYSADMINACCOUNTS=`"@@{sql_local.username}@@`" /INSTANCEID=MSSQLSERVER /INSTANCENAME=MSSQLSERVER /UPDATEENABLED=False /INDICATEPROGRESS /TCPENABLED=1 /SQLTEMPDBDIR=`"G:\TEMPDB`" /SQLTEMPDBLOGDIR=`"I:\TEMPDBLOG`" /SQLUSERDBDIR=`"H:\DATA`" /SQLUSERDBLOGDIR=`"J:\ULOG`" /INSTALLSQLDATADIR=`"${DriveLetter}:\SQLINSTALL`""
$setupDriveLetter = "D:"
$setupPath = "$setupDriveLetter\setup.exe"

########################################################

$timeout = 10 # 15 minutes
$i = 0
$taskAction = New-ScheduledTaskAction -Execute $setupPath -Argument $silentArgs
#$taskAction = New-ScheduledTaskAction -Execute 'c:\windows\system32\cmd.exe' -Argument '/c net user'

$trigger =  New-ScheduledTaskTrigger -Once -At (Get-Date).AddSeconds(5)

Register-ScheduledTask -Action $taskAction -Trigger $trigger -TaskName SQLInstall -Description "Install SQL" -User "@@{windows_local.username}@@" -Password "@@{windows_local.secret}@@"

do
{

sleep(60)
echo "SQL Server installation in progress, please wait..."
$taskCode = (Get-ScheduledTaskInfo -TaskName "\SQLInstall").LastTaskResult
$state = (Get-ScheduledTask -TaskName SQLInstall).State
if($taskCode -eq 0) {
 echo "finished";
 Unregister-ScheduledTask -TaskName SQLInstall -Confirm:$false
 break 
 } elseif (($state -eq "Ready") -and ($taskCode -ne 0) )
{
 echo "task finished with error "#;continue 
 }

$i++
if($i > $timeout) {
echo "timeout";
Unregister-ScheduledTask -TaskName SQLInstall -Confirm:$false
break
}
} while (Get-ScheduledTask -TaskName SQLInstall | Where state -eq "Running")
