#! /bin/sh
source /etc/profile

# Get Hosts stats with ACLI
echo "AHV_STATS="$(acli -o 'json' host.get '*')
echo "AHV_LIST="$(acli -o 'json' host.list)

hosts_list='@@{AHV_LIST}@@'
host_uuid='@@{ahv_pinning_esx01_uuid}@@'

r=json.loads(hosts_list)
data=r['data']

for x in data:
    if x['uuid'] == host_uuid:
      print "ahv_pinning_esx01_name=", x['hypervisorAddress']
    else:
      continue

