def get_directory_uuid(domain_name):
  api_endpoint = '/api/nutanix/v3/directory_services/list'
  payload = {'filter': '', 'kind': 'directory_service'}
  dirs = http_request(api_endpoint, payload)
  for item in dirs['entities']:
