$source = "Z:\install\Adobe.exe"
$destination = "C:\install"
$testPath = "C:\install\Adobe.exe"

New-Item -Path "C:\install" -ItemType directory -Force
if (Test-Path -Path $source) {
		Write-Host "Source directory reachable, start copying"
    	Copy-Item -Path $source -Destination $destination -Recurse -Force
    } else {
    	Write-Host "Source not reachable"
    }


if (Test-Path -Path $testPath) {
    	powershell.exe c:\install\Adobe.exe /sAll /msi /norestart ALLUSERS=1 EULA_ACCEPT=YES
    	Write-Host -ForegroundColor Green "Installation successful"
    } else {
        Write-Host -ForegroundColor Red "Install failed"
    }

$source = "Z:\install\NetBackup"
$destination = "C:\install\NetBackup"
$testPath = "C:\install\NetBackup\x86\silentclient.cmd"

New-Item -Path "C:\install\NetBackup" -ItemType directory -Force
Get-ChildItem -Path $source | Copy-Item -Destination $destination -Recurse -Container

if (Test-Path -Path $testPath) {
    ((Get-Content -path C:\install\NetBackup\x86\silentclient.cmd -Raw) -replace 'SET CLIENT=%COMPUTER_NAME%','SET CLIENT=@@{vm_name}@@') | Set-Content -Path C:\install\NetBackup\x86\silentclient.cmd
    Write-Host -ForegroundColor Green "Replace computer name sucessfull"
    } else {
    Write-Host -ForegroundColor Red "Replace computer name unsucessful"}

if (cmd.exe /c $testPath) {
    Write-Host -ForegroundColor Green "NetBackup installation completed"
    } else {
    Write-Host -ForegroundColor Red "NetBackup installation failed"}

