# Set creds and headers
era_user = '@@{CRED_ERA.username}@@'
era_pass = '@@{CRED_ERA.secret}@@'
era_ip = '@@{ERA_IP}@@'
era_sla_name = '@@{ERA_SLA_NAME}@@'
timestamp = '@@{TIMESTAMP}@@'
db_name_prefix = '@@{DB_NAME_PREFIX}@@'
db_name = '@@{DB_NAME}@@'
cluster_id = '@@{CLUSTER_ID}@@'
software_prof_id = "@@{SOFTWARE_PROF_ID}@@"
software_prof_ver_id = "@@{SOFTWARE_PROF_VER_ID}@@"
compute_prof_id = "@@{COMPUTE_PROF_ID}@@"
network_prof_id = "@@{NETWORK_PROF_ID}@@"
db_param_id = "@@{DB_PARAM_ID}@@"
db_password = "@@{DB_PASSWORD}@@"
db_public_key = "@@{CRED_SERVER_DB.public_key}@@"
sla_id = "@@{SLA_ID}@@"

# ========= DO NOT CHANGE AFTER HERE ==========
headers = {'Content-Type': 'application/json', 'Accept': 'application/json'}

# Set the URL and payload
url     = "https://{}/era/v0.8/databases/provision".format(era_ip)
payload = {
  "databaseName": db_name,
  "databaseType": "postgres_database",
  "databaseDescription": "Postgres database provisioned by Calm Application @@{calm_application_name}@@",
  "clusterId": cluster_id,
  "softwareProfileId": software_prof_id,
  "softwareProfileVersionId": software_prof_ver_id,
  "computeProfileId": compute_prof_id,
  "networkProfileId": network_prof_id,
  "dbParameterProfileId": db_param_id,
  "newDbServerTimeZone": "Europe/Amsterdam",
  "provisionInfo": [
    {
      "name": "application_type",
      "value": "postgres_database"
    },
    {
      "name": "nodes",
      "value": "1"
    },
    {
      "name": "listener_port",
      "value": "5432"
    },
    {
      "name": "proxy_read_port",
      "value": "5001"
    },
    {
      "name": "proxy_write_port",
      "value": "5000"
    },
    {
      "name": "database_size",
      "value": "200"
    },
    {
      "name": "working_dir",
      "value": "/tmp"
    },
    {
      "name": "auto_tune_staging_drive",
      "value": True
    },
    {
      "name": "enable_synchronous_mode",
      "value": False
    },
    {
      "name": "db_password",
      "value": db_password
    },
    {
      "name": "dbserver_name",
      "value": "PostgreSQL-@@{calm_time}@@"
    },
    {
      "name": "dbserver_description",
      "value": "Postgres database server provisioned by Calm Application @@{calm_application_name}@@"
    },
    {
      "name": "ssh_public_key",
      "value": db_public_key
    }
  ],
  "timeMachineInfo": {
    "name": "PostgreSQL-@@{calm_time}@@_TM",
    "description": "PostgreSQL-@@{calm_time}@@ time machine",
    "slaId": sla_id,
    "schedule": {
      "continuousSchedule": {
        "enabled": True,
        "logBackupInterval": 30,
        "snapshotsPerDay": 30
      },
      "snapshotTimeOfDay": {
        "hours": 1,
        "minutes": 0,
        "seconds": 0
      },
      "weeklySchedule": {
        "enabled": True,
        "dayOfWeek": "SUNDAY"
      },
      "monthlySchedule": {
        "enabled": True,
        "dayOfMonth": 1
      },
      "quartelySchedule": {
        "enabled": True,
        "startMonth": "JANUARY",
        "dayOfMonth": 1
      },
      "yearlySchedule": {
        "enabled": False,
        "month": "DECEMBER",
        "dayOfMonth": 1
      }
    }
  }
}

# Make the call and set the response operation ID to the variable
resp = urlreq(url, verb='POST', auth='BASIC', user=era_user, passwd=era_pass, params=json.dumps(payload), headers=headers)
if resp.ok:
  print("CREATE_OPERATION_ID={}".format(json.loads(resp.content)['operationId']))
else:
  print("Post Database create request failed", json.dumps(json.loads(resp.content), indent=4))
  exit(1)

kube_calm_sa_token = '@@{KUBE_CALM_SA_TOKEN}@@'
kubemaster_ip = '@@{KUBEMASTER_IP}@@'
kubernetes_port = @@{KUBEMASTER_PORT}@@
db_password = "@@{DB_PASSWORD}@@"
db_server_ip = "@@{DB_SERVER_IP}@@"
db_name = "@@{DB_NAME}@@"
objects_access_key = "@@{CRED_OBJECTS.username}@@"
objects_secret_key = "@@{CRED_OBJECTS.secret}@@"
bucket_name = "@@{BUCKET_NAME}@@"
objects_ip = "@@{OBJECTS_IP}@@"

# ========= DO NOT CHANGE AFTER HERE ==========
headers = {'Content-Type': 'application/json', 'Accept': 'application/json', 'Authorization': 'Bearer {}'.format(kube_calm_sa_token)}

url     = "https://{}:{}/apis/batch/v1/namespaces/default/jobs".format(kubemaster_ip,kubernetes_port)

payload = {
  "apiVersion": "batch/v1",
  "kind": "Job",
  "metadata": {
    "name": "oscar-django-migrations-@@{calm_application_uuid}@@",
    "labels": {
      "app": "django",
      "component": "oscar",
      "tier": "frontend"
    }
  },
  "spec": {
    "template": {
      "metadata": {
        "labels": {
          "app": "django",
          "component": "oscar",
          "tier": "frontend"
        }
      },
      "spec": {
        "containers": [
          {
            "name": "django",
            "image": "ukdemo/oscar_jet:latest",
            "imagePullPolicy": "Always",
            "command": [
              "/bin/bash",
              "migrate.sh"
            ],
            "env": [
              {
                "name": "DATABASE_USER",
                "value": "postgres"
              },
              {
                "name": "DATABASE_PASSWORD",
                "value": db_password
              },
              {
                "name": "DATABASE_HOST",
                "value": db_server_ip
              },
              {
                "name": "DATABASE_NAME",
                "value": db_name
              },
              {
                "name": "ACCESS_KEY",
                "value": objects_access_key
              },
              {
                "name": "SECRET_ACCESS_KEY",
                "value": objects_secret_key
              },
              {
                "name": "STATIC_BUCKET",
                "value": bucket_name
              },
              {
                "name": "S3_ENDPOINT_URL",
                "value": "http://{}/".format(objects_ip)
              }
            ]
          }
        ],
        "restartPolicy": "Never"
      }
    },
    "backoffLimit": 5
  }
}


resp = urlreq(url, verb='POST', params=json.dumps(payload), headers=headers)

if resp.ok:
  print(json.dumps(json.loads(resp.content), indent=4))
else:
  print("Post K8S Job request failed", json.dumps(json.loads(resp.content), indent=4))
  exit(1)

pvc_name = 'redis-@@{Postgres.TIMESTAMP}@@'
kube_namespace = '@@{KUBE_NAMESPACE}@@'.lower()
kubemaster_ip = '@@{KUBEMASTER_IP}@@'
kubemaster_port = @@{KUBEMASTER_PORT}@@
kube_calm_sa_token = '@@{KUBE_CALM_SA_TOKEN}@@'
kube_pvc_accessMode = 'ReadWriteOnce'
kube_pvc_size = '20Gi'
kube_pvc_storageClass = 'nutanix-volume'

url     = "https://{}:{}/api/v1/namespaces/{}/persistentvolumeclaims".format(kubemaster_ip,kubemaster_port,kube_namespace)
headers = {'Content-Type': 'application/json', 'Accept': 'application/json', 'Authorization': 'Bearer {}'.format(kube_calm_sa_token)}

payload = {
  "apiVersion": "v1",
  "kind": "PersistentVolumeClaim",
  "metadata": {
    "name": pvc_name,
    "namespace": kube_namespace
  },
  "spec": {
    "accessModes": [
      kube_pvc_accessMode
    ],
    "resources": {
      "requests": {
        "storage": kube_pvc_size
      }
    },
    "storageClassName": kube_pvc_storageClass,
    "volumeMode": "Filesystem"
  }
}


resp = urlreq(url, verb='POST', params=json.dumps(payload), headers=headers)

if resp.ok:
  print(json.dumps(json.loads(resp.content), indent=4))
  print("REDIS_PVC_NAME={}".format(pvc_name))
else:
  print("Post K8S Job request failed", json.dumps(json.loads(resp.content), indent=4))
  exit(1)

