#!/bin/bash -xe

MACHINE_IP="@@{OpenshiftLB_DNS.address}@@"
MACHINE_NETWORK="@@{MACHINE_NETWORK}@@"
OPENSHIFT_SUBDOMAIN="@@{OPENSHIFT_SUBDOMAIN}@@"
BASE_DOMAIN="@@{BASE_DOMAIN}@@"
RDNS_RANGE="${MACHINE_NETWORK%.*}"
#REV_ZONE_RANGE=$(python3 -c 'print((".").join("'${RDNS_RANGE}'".split(".")[::-1]))')
REV_ZONE_RANGE=$(echo ${RDNS_RANGE} | awk -F '.' '{OFS="."; print $3,$2,$1}')
MACHINE_IP_SUFFIX="${MACHINE_IP##*.}"

### Bind DNS server configuration ###
# Create forward and reverse lookup zones for Openshift
cat <<EOF | sudo tee -a /etc/named.conf
zone "@@{BASE_DOMAIN}@@" IN {
        type master;
        file "db.${OPENSHIFT_SUBDOMAIN}.${BASE_DOMAIN}";
};


zone "${REV_ZONE_RANGE}.in-addr.arpa" IN {
        type master;
        file "db.${REV_ZONE_RANGE}.in-addr.arpa";
};
EOF

# Listen on all interfaces
sudo sed -i '/listen-on port 53/ s/127.0.0.1/any/g' /etc/named.conf
# Allow DNS lookup from all IPs
sudo sed -i '/allow-query/ s:localhost:any:g' /etc/named.conf

# # Allow DNS lookup from all IPs in MACHINE_NETWORK range
# sudo sed -i '/allow-query/ s:localhost:'${MACHINE_NETWORK}':g' /etc/named.conf

# Set forwarders for lookup of external zones
# Defaults to Google nameservers
cat <<EOF > /tmp/forwarders.txt
        // Set forwarders to DNS zone
        forwarders {
                @@{DNS_FORWARDER}@@;
        };
EOF
sudo sed -i.bkp '/recursion yes/r /tmp/forwarders.txt' /etc/named.conf

# Create forward lookup zone templates
cat <<EOF | sudo tee /var/named/db.${OPENSHIFT_SUBDOMAIN}.${BASE_DOMAIN}
\$TTL 1W
@   IN  SOA ns1.${BASE_DOMAIN}.    root (
            2019070703  ; serial
            3H      ; refresh (3 hours)
            30M     ; retry (30 minutes)
            2W      ; expiry (2 weeks)
            1W )        ; minimum (1 week)
    IN  NS  ns1.${BASE_DOMAIN}.
;
;
ns1 IN  A   ${MACHINE_IP}
;
helper  IN  A   ${MACHINE_IP}
helper.${OPENSHIFT_SUBDOMAIN} IN  A   ${MACHINE_IP}
;
; The api points to the IP of your load balancer
api.${OPENSHIFT_SUBDOMAIN}        IN  A   ${MACHINE_IP}
api-int.${OPENSHIFT_SUBDOMAIN}        IN  A   ${MACHINE_IP}
;
; The wildcard also points to the load balancer
*.apps.${OPENSHIFT_SUBDOMAIN}     IN  A   ${MACHINE_IP}
;
; Bootstrap host
bootstrap.${OPENSHIFT_SUBDOMAIN}  IN  A   0.0.0.0
; Master hosts
master0.${OPENSHIFT_SUBDOMAIN}        IN  A   1.0.0.0
master1.${OPENSHIFT_SUBDOMAIN}        IN  A   1.0.0.1
master2.${OPENSHIFT_SUBDOMAIN}        IN  A   1.0.0.2
;
; Worker hosts
; worker0.${OPENSHIFT_SUBDOMAIN}        IN  A   2.0.0.0
; worker1.${OPENSHIFT_SUBDOMAIN}        IN  A   2.0.0.1
;
; The ETCd cluster lives on the masters
etcd-0.${OPENSHIFT_SUBDOMAIN} IN  A   1.0.0.0
etcd-1.${OPENSHIFT_SUBDOMAIN} IN  A   1.0.0.1
etcd-2.${OPENSHIFT_SUBDOMAIN} IN  A   1.0.0.2
;
; SRV records
_etcd-server-ssl._tcp.${OPENSHIFT_SUBDOMAIN}  IN  SRV 0 10 2380 etcd-0.${OPENSHIFT_SUBDOMAIN}.${BASE_DOMAIN}.
_etcd-server-ssl._tcp.${OPENSHIFT_SUBDOMAIN}  IN  SRV 0 10 2380 etcd-1.${OPENSHIFT_SUBDOMAIN}.${BASE_DOMAIN}.
_etcd-server-ssl._tcp.${OPENSHIFT_SUBDOMAIN}  IN  SRV 0 10 2380 etcd-2.${OPENSHIFT_SUBDOMAIN}.${BASE_DOMAIN}.
EOF

cat <<EOF | sudo tee /var/named/db.${REV_ZONE_RANGE}.in-addr.arpa
\$TTL 1W
@   IN  SOA ns1.${BASE_DOMAIN}.    root (
            2019070700  ; serial
            3H      ; refresh (3 hours)
            30M     ; retry (30 minutes)
            2W      ; expiry (2 weeks)
            1W )        ; minimum (1 week)
    IN  NS  ns1.${BASE_DOMAIN}.
;
; syntax is "last octet" and the host must have fqdn with trailing dot
10  IN  PTR master0.${OPENSHIFT_SUBDOMAIN}.${BASE_DOMAIN}.
11  IN  PTR master1.${OPENSHIFT_SUBDOMAIN}.${BASE_DOMAIN}.
12  IN  PTR master2.${OPENSHIFT_SUBDOMAIN}.${BASE_DOMAIN}.
;
00  IN  PTR bootstrap.${OPENSHIFT_SUBDOMAIN}.${BASE_DOMAIN}.
;
${MACHINE_IP_SUFFIX}  IN  PTR api.${OPENSHIFT_SUBDOMAIN}.${OPENSHIFT_SUBDOMAIN}.${BASE_DOMAIN}.
${MACHINE_IP_SUFFIX}   IN  PTR api-int.${OPENSHIFT_SUBDOMAIN}.${OPENSHIFT_SUBDOMAIN}.${BASE_DOMAIN}.
;
;20  IN  PTR worker0.${OPENSHIFT_SUBDOMAIN}.${BASE_DOMAIN}.
;21  IN  PTR worker1.${OPENSHIFT_SUBDOMAIN}.${BASE_DOMAIN}.
EOF

# Enable bind on startup
sudo systemctl enable named
sudo systemctl start named

subnet_ref={
                "kind": "subnet",
                "uuid": "@@{SUBNET_REF}@@"
              }
print 'subnet_ref=',json.dumps(subnet_ref)

