#! /bin/sh
source /etc/profile

# Get Hosts stats with ACLI
echo "hosts_stats="$(acli -o 'json' host.get '*')
echo "hosts_list="$(acli -o 'json' host.list)
echo $hosts_stats
echo $hosts_list

hosts_list='@@{hosts_list}@@'
host_uuid='@@{host_uuid}@@'

r=json.loads(hosts_list)
data=r['data']

for x in data:
    if x['uuid'] == host_uuid:
      print "host_name=", x['hypervisorAddress']
    else:
      continue

