attrs.script:
$recordsList = @(
    [pscustomobject]@{
        recordName='@@{calm_application_name}@@'; 
        zoneName='@@{domainName}@@'; 
        recordIpAddress='@@{vm_ip}@@'
    }
)

foreach ($recordObject in $recordsList){
    Remove-DnsServerResourceRecord `
        -Name $recordObject.recordName `
        -ZoneName $recordObject.zoneName `
        -RRType "A" `
        -RecordData $recordObject.recordIpAddress `
        -force
}

$recordsList = @(
    [pscustomobject]@{
        recordName='@@{calm_application_name}@@'; 
        zoneName='@@{domainName}@@'; 
        recordIpAddress='@@{vm_ip}@@'
    }
)

foreach ($recordObject in $recordsList){
    Add-DnsServerResourceRecordA `
        -Name $recordObject.recordName `
        -ZoneName $recordObject.zoneName `
        -AllowUpdateAny `
        -IPv4Address $recordObject.recordIpAddress `
        -CreatePtr
}

