attrs.script:
db_motor= "@@{database_motor}@@"
db_version= str("@@{database_version}@@")
if db_motor == "Oracle":
  print("12.1.0,12.2.0,18.0.0,19.0.0")
elif db_motor == "Postgresql":
  maj_version = db_version[:2]
  app_path = "/usr/pgsql-"+ maj_version
  print('app_dir_path = '+ app_path +'')

elif db_motor == "MySQL":
  app_path = "/software/mysql-"+ db_version
  print('app_dir_path = '+ app_path +'')

elif db_motor == "MongoDB":
  app_path = "/software/mysql-"+ db_version
  print('app_dir_path = '+ app_path +'')

else:
  print("error")

# Set creds and headers
era_user = "@@{cred_era_admin_password.username}@@"
db_type = "@@{database_motor}@@"
version = "@@{database_version}@@"
era_app_reg_path = "@@{app_dir_path}@@"
era_pass = "@@{cred_era_admin_password.secret}@@"
headers = {"Content-Type": "application/json", "Accept": "application/json"}
print "Monitoring Register DatabaseVM Operation..."
url = "https://@@{era_ip}@@/era/v0.9/dbservers/register"


if db_type == "MySQL":
    # Set the URL and payload
    payload = {
        "actionArguments": [
            {
            	"name": "listener_port",
                "value": "3306"
            },
            {
            	"name": "software_home",
                "value": '"'+ era_app_reg_path +'"'
            }
        ],
        "vmIp": "@@{address}@@",
        "nxClusterUuid": "@@{CLUSTER_ID}@@",
        "databaseType": "mysql_database",
        "forcedInstall": "true",
        "workingDirectory": "/tmp",
        "username": "@@{cred_era_admin.username}@@",
        "password": "@@{cred_era_admin.secret}@@",
    }

elif db_type == "Postgresql":
    maj_version = version[:2]
    payload = {
        "actionArguments": [
           {
            	"name": "listener_port",
                "value": "5432"
            },
            {
            	"name": "software_home",
                "value": '"'+ era_app_reg_path +'"'
            }
        ],
        "vmIp": "@@{address}@@",
        "nxClusterUuid": "@@{CLUSTER_ID}@@",
        "databaseType": "postgres_database",
        "forcedInstall": "true",
        "workingDirectory": "/tmp",
        "username": "@@{cred_era_admin.username}@@",
        "password": "@@{cred_era_admin.secret}@@",
    }

# Make the call and set the response operation ID to the variable
resp = urlreq(
    url,
    verb="POST",
    auth="BASIC",
    user=era_user,
    passwd=era_pass,
    params=json.dumps(payload),
    headers=headers,
)
if resp.ok:
    print("REGISTER_OPERATION_ID={0}".format(json.loads(resp.content)["operationId"]))
else:
    print("Database VM Register failed", json.dumps(json.loads(resp.content), indent=4))
    exit(1)

if "@@{database_motor}@@" == "Oracle":
  print("oracle dbrms binaries")
elif "@@{database_motor}@@" == "Postgresql":
  print("RPM,COMPILE")
elif "@@{database_motor}@@" == "MySQL":
  print("NUXPRESSO")
elif "@@{database_motor}@@" == "MongoDB":
  print("NUXPRESSO")
elif "@@{database_motor}@@" == "MariaDB":
  print("NUXPRESSO")
else:
  print("NUXPRESSO")

