vpc_name="@@{getVpc}@@"
subnet_name="@@{getVpcSubnet}@@"
pc_admin = "nx2Tech253!"
print(vpc_name)
print(subnet_name)

pc_url="https://127.0.0.1:9440/api/nutanix/"

def get_resource_list(url, params):
    headers = {'Content-Type': 'application/json',  'Accept':'application/json'}
    try:
        r=urlreq(url, verb='POST', params=json.dumps(params), headers=headers, auth="BASIC", user="admin", passwd= pc_admin, verify=False)
        resp=json.loads(r.content)
    except Exception as e:
        print e
    return resp

def get_vpc_uuid(vpc_name):
    vpc_url = pc_url+"v3/vpcs/list"
    params={"kind": "vpc"}
    resp = get_resource_list(vpc_url, params)
    for vpc in resp["entities"]:
        if vpc["spec"]["name"]==vpc_name:
            return vpc["metadata"]["uuid"]

def get_subnet_uuid(network_name, vpc_uuid):
    subnet_url = pc_url+"v3/subnets/list"
    params={"kind": "subnet"}
    resp = get_resource_list(subnet_url, params)
    for subnet in resp["entities"]:
        if subnet["spec"]["name"]==network_name and subnet["spec"]["resources"]["vpc_reference"]["uuid"]==vpc_uuid:
            return subnet["metadata"]["uuid"]

vpc_uuid=get_vpc_uuid(vpc_name)
if vpc_uuid=="" or not vpc_uuid:
    print "VPC {} doesn't exist".format(vpc_name)
    exit(1)
subnet_uuid=get_subnet_uuid(subnet_name, vpc_uuid)
if subnet_uuid=="" or not subnet_uuid:
    print "Subnet {} doesn't exist".format(subnet_name)
    exit(1)

subnet={
  "name":subnet_name,
  "uuid":subnet_uuid
}
print "subnet={}".format(json.dumps(subnet))

